pragma solidity ^0.4.24;

import "./CompoundUser.sol";
import "./EIP20Interface.sol";

// too be inheritd from by asset specific borrow contracts
contract BorrowFactory {
  address public assetAddress;
  mapping(address => address) public borrowers;
  /* address constant wethAddress =  */
  // mapping of quickborrowers to their user contracts

  constructor(address asset) public {
    assetAddress = asset;
  }

  function() payable public {
    /* assert borrowers[msg.sender] == null */
    // intialize a "compound user",
    // and funds it with eth sent
    CompoundUser user = (new CompoundUser).value(msg.value)() ;
    borrowers[msg.sender] = user;
    /* authorize this contract and compound money market to transfer weth and requested borrow */
    user.authorize(assetAddress);
    /* user.authorize(wethAddress); */

    // supply all the WETH
    /* user.wrapWETH(); */
    /* user.supply(wethAddress); */

    // borrow MAX ?
    user.borrow(assetAddress, 5);

    // send tokens to owner

    EIP20Interface token = EIP20Interface(assetAddress);
    token.transferFrom(user, msg.sender, 5);
  }

  // meatspace user must approve this contract to transfer the token
  function repay() public {
    CompoundUser user = CompoundUser(borrowers[msg.sender]);

    EIP20Interface token = EIP20Interface(assetAddress);
    // necessary to approve tokens before calling repay
    token.transferFrom(msg.sender, user, 5);

    user.repay(assetAddress, 5);
    /* user.withdraw(wethAddress); */
    /* user.unwrapWETH(); */
    /* user.returnEth(msg.sender); */

    /* delete borrowers[msg.sender]; // free to borrow again */
  }
}
